import java.util.Scanner;

public class Robot extends SentientBeing
{
	public void Move(Place place) 
	{
		System.out.println("Move(Place place)");
		this.location.DropBeing(this);
		place.RegisterBeing(this);
	}
	
	public void Drill()
	{
		System.out.println("Drill()");
		System.out.println("Még tudja fúrni a telepes?(igen/nem)");
		Scanner scanner = new Scanner(System.in);
		String input = scanner.nextLine();
		if(input.equals("igen"))
		{
			location.IncreaseHoleDepth();
		}
		scanner.close();
	}	
	
	public void Die() 
	{
		System.out.println("Die()");
		location.DropBeing(this);
	}
	
	public void Explode()
	{
		System.out.println("Explode()");
		Scanner scanner = new Scanner(System.in);
		int input = 0;
		System.out.println("Hány darab szomszédja van az aszteroidának? (0-több)");
		input = scanner.nextInt();
		scanner.close();
		if(input == 0) {
			Die();
		} else {
			Move(null);
		}
				
	}
	
	public void Step() 
	{
		System.out.println("Step()");
		Scanner scanner = new Scanner(System.in);
		System.out.println("Fúrjon vagy lépjen a robot?");
		String input = scanner.nextLine();
		scanner.close();
		if(input.equals("fúr")) {
			this.Drill();
		} else if(input.equals("lép")) {
			this.Move(null);
		}
	}
}
